CREATE TABLE `t_fa_account_situation` (
  `SITUATION_ID` varchar(50) NOT NULL COMMENT '资金概况主键id',
  `SALES_AREA_CODE` varchar(50) DEFAULT NULL COMMENT '销售大区Code',
  `SALES_AREA_NAME` varchar(50) DEFAULT NULL COMMENT '销售大区名称',
  `SALES_VILLAGE_CODE` varchar(50) DEFAULT NULL COMMENT '销售小区Code',
  `SALES_VILLAGE_NAME` varchar(50) DEFAULT NULL COMMENT '销售小区名称',
  `BUYER_ID` int(20) DEFAULT NULL COMMENT '客户id',
  `BUYER_NAME` varchar(50) DEFAULT NULL COMMENT '客户名称',
  `SELLER_ID` int(20) DEFAULT NULL COMMENT '卖家id',
  `SELLER_NAME` varchar(50) DEFAULT NULL COMMENT '卖家名称',
  `BALANCE` decimal(20,6) DEFAULT NULL COMMENT '账户余额',
  `USE_AMOUNT` decimal(20,6) DEFAULT NULL COMMENT '余额占用',
  `AVAILABLE_BALANCE` decimal(20,6) DEFAULT NULL COMMENT '当前可用金额',
  `BUYER_CODE` varchar(20) DEFAULT NULL COMMENT '客户编码',
  `CATAGORY_NAME` varchar(50) DEFAULT NULL COMMENT '产品中类名称',
  `CATAGORY_CODE` varchar(20) DEFAULT NULL COMMENT '产品中类code',
  `BU_NAME` varchar(50) DEFAULT NULL COMMENT '事业部名称',
  `BU_CODE` varchar(20) DEFAULT NULL COMMENT '事业部code',
  `CREATE_PERSON` varchar(50) DEFAULT NULL COMMENT '创建人',
  `CREATE_DATE` datetime DEFAULT NULL COMMENT '创建时间',
  `MODI_PERSON` varchar(50) DEFAULT NULL COMMENT '修改人',
  `MODI_DATE` datetime DEFAULT NULL COMMENT '修改时间',
  `DELETE_PERSON` varchar(50) DEFAULT NULL COMMENT '删除人',
  `DELETE_DATE` datetime DEFAULT NULL COMMENT '删除时间',
  `EXTEND1` varchar(50) DEFAULT NULL COMMENT '扩展字段1',
  `EXTEND2` datetime DEFAULT NULL COMMENT '扩展字段2',
  `EXTEND3` decimal(18,6) DEFAULT NULL COMMENT '扩展字段3',
  `BUYER_SAP_CODE` varchar(50) DEFAULT NULL COMMENT '买家SAPCode(MDM编码)',
  `CASH_DEBT` decimal(20,6) DEFAULT NULL COMMENT '直销现款欠款金额',
  `CASH_PAY` decimal(20,6) DEFAULT NULL COMMENT '直销现款还款金额',
  `CASH_NO_PAY` decimal(20,6) DEFAULT NULL COMMENT '直销现款未还金额',
  PRIMARY KEY (`SITUATION_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='卖家对应买家余额概况';

